import Mock from "mockjs";

export default {
    getMenu: config => {
        const { mail, password } = JSON.parse(config.body)
        // 判断用户是否存在
        if (mail === 'admin@kth.se' && password === 'password') {
            return {
                code: 20000,
                data: {
                    menu: [
                        {
                            path:'/home',
                            name:'home',
                            label:'首页',
                            icon:'s-home',
                            url:'home/index'
                        },
                        {
                            path:'/video',
                            name:'video',
                            label:'视频管理页',
                            icon:'video-play',
                            url:'video/index'
                        },
                        {
                            path:'/voice',
                            name:'voice',
                            label:'语音管理页',
                            icon:'voice',
                            url:'voice/index'

                        },
                        {
                            label:'其他',
                            icon:'location',
                            children:[
                                {
                                    path:'/page1',
                                    name:'page1',
                                    label:'页面1',
                                    icon:'setting',
                                    url:'other/page1'
                                },
                                {
                                    path:'/page2',
                                    name:'page2',
                                    label:'页面2',
                                    icon:'setting',
                                    url:'other/page2'
                                }
                            ]
                        }
                    ],
                    token: Mock.Random.guid(),
                    message: '获取成功'
                } 
            }
        } else{
                return {
                    code: -999,
                    data: {
                        message: '账号或密码错误'
                    }

                }
            }
    }
}
